import checkService from "./API/checkService";
import {useEffect, useState} from "react";
import './styles/App.css'

import FormRegister from "./Components/FormRegister/FormRegister";
import ManageArrow from "./Components/ManageArrow/ManageArrow";
import ChooseArea from "./Components/ChooseArea/ChooseArea";
function App() {

    const [courses, setCourses] = useState([])
    const [course, setCourse] = useState({})
    const [groups, setGroups] = useState([])
    const [group, setGroup] = useState("")
    const [labs, setLabs] = useState([])
    const [lab, setLab] = useState("")
    const [message, setMessage] = useState("")
    const [fullUserData, setFullUserData] = useState({surname: "", name: "", patronymic: "", github: ""})

    const [pageChoose, setPageChoose] = useState(1)

    const allOpt = [course, group, lab]


    const callback = () => {
        checkService.postRegister(course, group, {...fullUserData}).then(async a => {
            console.log(a)
            if (a.status === 202) {
                setPageChoose(pageChoose + 1)
                const mes = await checkService.postGrade(course, group, lab)
                console.log(mes.message)
            }
        })
    }

    useEffect(() => {
        if(pageChoose === 1){
            if (courses.length === 0) {
                checkService.getCourses().then(a => setCourses(a))
            }
            setGroups([]);
            setGroup([])
        }
        else if(pageChoose === 2){
            if (groups.length === 0) {
                checkService.getGroups(course.id).
                then(res => {setGroups(res)
                    setCourses([])
                })
            }

            setLabs([])
            setLab("")
        }
        else if(pageChoose === 3){
            if (labs.length === 0) {
                checkService.getLabs(group).
                then(res => {setLabs(res)
                    setGroups([])
                })
            }
            setFullUserData({surname: "", name: "", patronymic: "", github: ""})
            document.getElementsByClassName("form")[0].style = "display: none"
        }
        else if(pageChoose === 4){
            setLabs([])
            document.getElementsByClassName("form")[0].style = "display: block"
        }
    }, [pageChoose])

    return (
    <div className="App">
        <div>{pageChoose}</div>
        <div>{group}</div>
        <div>{course.name}</div>
        <div>{lab}</div>

        <div className={"chooseParentDiv"}>
            <div className={"chooseDiv"}>
                <ChooseArea groups={groups} setGroup={group} courses={courses} setCourse={setCourse} labs={labs} setLab={setLab}/>
                <FormRegister fullUserData={fullUserData} setFullUserData={setFullUserData}/>
                <ManageArrow allOpt={allOpt} setPageChoose={setPageChoose} pageChoose={pageChoose} callback={callback}/>
            </div>
        </div>

    </div>
    );
}

export default App;
