import checkService from "./API/checkService";
import {useEffect, useState} from "react";
import {useNavigate} from 'react-router-dom'
import './styles/App.css'


import FormRegister from "./Components/FormRegister/FormRegister";
import ManageArrow from "./Components/ManageArrow/ManageArrow";
import ChooseArea from "./Components/ChooseArea/ChooseArea";
import Background from "./Components/Background/Background";
function App() {

    const [courses, setCourses] = useState([])
    const [course, setCourse] = useState({id: "", name: "", semester: ""})
    const [groups, setGroups] = useState([])
    const [group, setGroup] = useState("")
    const [labs, setLabs] = useState([])
    const [lab, setLab] = useState("")
    const [message, setMessage] = useState("")
    const [result, setResult] = useState("")
    const [fullUserData, setFullUserData] = useState({surname: "", name: "", patronymic: "", github: ""})
    const [filter, setFilter] = useState("")
    const [pageChoose, setPageChoose] = useState(1)

    const allOpt = [course, group, lab]



    const callback = () => {
        checkService.postRegister(course, group, {...fullUserData}).then(async a => {
            console.log(a)
            setMessage(a.message)
            if (a.status === 202) {
                setPageChoose(pageChoose + 1)
            }

        })
    }

    const FetchInfo = () => {
        if(pageChoose === 1){
            if (courses.length === 0) {
                checkService.getCourses().then(a => setCourses(a))
            }
            setGroups([]);
            setGroup("")
            setLabs([])
            setLab("")
            setMessage("")
            setResult("")

        }
        else if(pageChoose === 2){

            if (groups.length === 0) {
                checkService.getGroups(course.id).
                then(res => {setGroups(res)
                    setCourses([])
                })
            }
            setLabs([])
            setLab("")
            setMessage("")
            setResult("")
        }
        else if(pageChoose === 3){
            if (labs.length === 0) {
                checkService.getLabs(group).
                then(res => {
                    setLabs(res)
                    setGroups([])
                })
            }
            setMessage("")
            setResult("")
            setFullUserData({surname: "", name: "", patronymic: "", github: ""})
            document.getElementsByClassName("form")[0].style = "display: none"
        }
        else if(pageChoose === 4){
            setLabs([])
            setMessage("")
            setResult("")
            document.getElementsByClassName("form")[0].style = "display: flex"
            document.getElementsByClassName("chooseDiv")[0].style = "overflow-y: scroll"
        }
        else if(pageChoose === 5){
            document.getElementsByClassName("form")[0].style = "display: none"
            document.getElementsByClassName("chooseDiv")[0].style = "overflow: hidden"
            checkService.postGrade(course, group, lab, fullUserData.github).then(a => {
                setResult(a.message)
            })

        }


    }

    useEffect(() => {
        FetchInfo()
    }, [pageChoose])

    return (
    <div className="App">


        <div className={"context"}>



        </div>


        <Background/>

    </div>
    );
}


export default App;
