import React from 'react';
import ar from "../../utils/img/arrow.png";
import cl from "./ManageArrow.module.css"

const ManageArrow = ({pageChoose, setPageChoose, allOpt, callback}) => {


    const checkOppNext = () => {
        if (pageChoose < 4) {
            if(pageChoose === 1) {
                if (allOpt[pageChoose - 1].length > 0 || allOpt[pageChoose - 1].name.length !== 0 ) {

                    return true
                }
            }
            else {
                if (allOpt[pageChoose - 1].length > 0) {

                    return true
                }
            }
        }
    }

    return (
        <div className={"arrowDiv"}>


            <div className={cl.textBut + " " +  cl.tbLeft} onClick={() =>
            {
                if (pageChoose!==1) setPageChoose(pageChoose-1)

            }

            }>
                <div className={pageChoose>1 ? cl.arDivL + " " + cl.actB : cl.arDivL }>
                    <img src={ar} style={{height: "100%"}} alt={"arrow to back"} />
                </div>

                Назад
            </div>

            <div className={cl.progress}>
                <div className={pageChoose>=1 ? cl.active : cl.desActive} onClick={() => {
                    pageChoose > 1 ? setPageChoose(1) : null

                }}></div>
                <div className={pageChoose>=2 ? cl.active : cl.desActive} onClick={() => {setPageChoose(2)}}></div>
                <div className={pageChoose>=3 ? cl.active : cl.desActive} onClick={() => {setPageChoose(3)}}></div>
                <div className={pageChoose>=4 ? cl.active : cl.desActive} onClick={() => {setPageChoose(4)}}></div>
                <div className={pageChoose>=5 ? cl.active : cl.desActive} onClick={() => {setPageChoose(5)}}></div>
            </div>

            <div className={cl.textBut + " " + cl.tbRight} onClick={() => {
                if (pageChoose < 4) {
                    if(pageChoose === 1) {
                        if (allOpt[pageChoose - 1].length > 0 || allOpt[pageChoose - 1].name.length !== 0 ) {

                            setPageChoose(pageChoose + 1)
                        }
                    }
                    else {
                        if (allOpt[pageChoose - 1].length > 0) {

                            setPageChoose(pageChoose + 1)
                        }
                    }
                }
                else{

                    callback()
                }
            }}>Далее
                <div className={checkOppNext() ? cl.arDivR + " " + cl.actB :  cl.arDivR}>
                    <img src={ar} style={{rotate: "180deg", height: "100%"}} alt = {"arrow to next"} />
                </div>

            </div>


        </div>
    );
};

export default ManageArrow;
