import checkService from "./API/checkService";
import {useEffect, useState} from "react";
import './styles/App.css'
import ar from './utils/img/arrow.png'
function App() {

    const [service, setService] = useState([])
    const [course, setCourse] = useState({})
    const [groups, setGroups] = useState([])
    const [group, setGroup] = useState("")
    const [labs, setLabs] = useState([])
    const [lab, setLab] = useState("")

    const [pageChoose, setPageChoose] = useState(1)

    const allOpt = [course, group]

    useEffect(() => {
        checkService.getCourses().then(a => setService(a))
    }, [])

    useEffect(() => {
        if(pageChoose === 1){
            checkService.getCourses().then(a => setService(a))
            setGroups([]);
            setGroup([])
        }
        else if(pageChoose === 2){
            checkService.getGroups(course.id).
            then(res => {setGroups(res)
                setService([])
            })
            setLabs([])
            setLab("")
        }
        else if(pageChoose === 3){
            checkService.getLabs(group).
            then(res => {setLabs(res)
                setGroups([])
            })
        }
    }, [pageChoose])

    return (
    <div className="App">
        <div>{pageChoose}</div>
        <div>{group}</div>
        <div>{course.name}</div>
        <div>{lab}</div>

        <div className={"chooseParentDiv"}>
            <div className={"chooseDiv"}>
                {service.map((a) => <div key={a.id} className={"course"} onClick={() => setCourse(a)}>{a.name}</div>)}
                {groups.map((a) => <div key={a} className={"course"} onClick={(e) => {
                    setGroup(a)}
                }>{a}</div>)}
                {labs.map((a) => <div key={a} className={"course"} onClick={(e) => {
                    setLab(a)}
                }>{a}</div>)}

                <div className={"form"}>
                    <div>
                        <div><input placeholder={""}/></div>
                    </div>
                </div>

                <div className={"arrowDiv"}>
                    <img src={ar} style={{width: "20%"}} alt={"arrow to next"} onClick={() => {setPageChoose(pageChoose-1)}}/>
                    <img src={ar} style={{rotate: "180deg", width: "20%"}} alt = {"arrow to back"} onClick={() => {
                        if(Object.keys(allOpt[pageChoose-1]).length !== 0 || allOpt[pageChoose-1].length > 0){
                        console.log(group.length)
                        setPageChoose(pageChoose+1)}}
                    }/>
                </div>


            </div>
        </div>

    </div>
    );
}

export default App;
