import React from 'react';

const ChooseArea = ({allOpt, courses, setCourse, groups, setGroup, labs,setLab, strfilt}) => {
    return (
        <div className={"divOpts"}>
            {[...courses].filter(p => p.name.toLowerCase().includes(strfilt.toLowerCase())).map((a) => <div key={a.id} className={a.name === allOpt[0].name ? "options optAct" : "options"} onClick={() => setCourse(a)}>{a.name} - семестер: {a.semester}</div>)}
            {/*{courses.map((a) => <div key={a.id} className={"options"} onClick={() => setCourse(a)}>{a.name} - семестер: {a.semester}</div>)}*/}
            {[...groups].filter(p=>p.toLowerCase().includes(strfilt.toLowerCase())).map((a) => <div key={a} className={"options"} onClick={(e) => {
                setGroup(a)}
            }>{a}</div>)}
            {/*{groups.map((a) => <div key={a} className={"options"} onClick={(e) => {*/}
            {/*    setGroup(a)}*/}
            {/*}>{a}</div>)}*/}
            {[...labs].filter(p=>p.toLowerCase().includes(strfilt.toLowerCase())).map((a) => <div key={a} className={"options"} onClick={(e) => {
                setLab(a)}
            }>{a}</div>)}
            {/*{labs.map((a) => <div key={a} className={"options"} onClick={(e) => {*/}
            {/*    setLab(a)}*/}
            {/*}>{a}</div>)}*/}

        </div>
    );
};

export default ChooseArea;
