import checkService from "./API/checkService";
import {useEffect, useState} from "react";
import './styles/App.css'
import ar from './utils/img/arrow.png'
import FormRegister from "./Components/FormRegister/FormRegister";
function App() {

    const [service, setService] = useState([])
    const [course, setCourse] = useState({})
    const [groups, setGroups] = useState([])
    const [group, setGroup] = useState("")
    const [labs, setLabs] = useState([])
    const [lab, setLab] = useState("")
    const [message, setMessage] = useState("")
    const [fullUserData, setFullUserData] = useState({surname: "", name: "", patronymic: "", github: ""})

    const [pageChoose, setPageChoose] = useState(1)

    const allOpt = [course, group, lab]

    useEffect(() => {
        if(pageChoose === 1){
            if (service.length === 0) {
                checkService.getCourses().then(a => setService(a))
            }
            setGroups([]);
            setGroup([])
        }
        else if(pageChoose === 2){
            if (groups.length === 0) {
                checkService.getGroups(course.id).
                then(res => {setGroups(res)
                    setService([])
                })
            }

            setLabs([])
            setLab("")
        }
        else if(pageChoose === 3){
            if (labs.length === 0) {
                checkService.getLabs(group).
                then(res => {setLabs(res)
                    setGroups([])
                })
            }
            setFullUserData({surname: "", name: "", patronymic: "", github: ""})
            document.getElementsByClassName("form")[0].style = "display: none"
        }
        else if(pageChoose === 4){
            setLabs([])
            document.getElementsByClassName("form")[0].style = "display: block"
        }
    }, [pageChoose])

    return (
    <div className="App">
        <div>{pageChoose}</div>
        <div>{group}</div>
        <div>{course.name}</div>
        <div>{lab}</div>

        <div className={"chooseParentDiv"}>
            <div className={"chooseDiv"}>
                {service.map((a) => <div key={a.id} className={"course"} onClick={() => setCourse(a)}>{a.name}</div>)}
                {groups.map((a) => <div key={a} className={"course"} onClick={(e) => {
                    setGroup(a)}
                }>{a}</div>)}
                {labs.map((a) => <div key={a} className={"course"} onClick={(e) => {
                    setLab(a)}
                }>{a}</div>)}

                <FormRegister full/>

                <div className={"arrowDiv"}>
                    <img src={ar} style={{width: "20%"}} alt={"arrow to next"} onClick={() =>
                        {
                            if (pageChoose!==1) setPageChoose(pageChoose-1)

                        }

                    }/>
                    <img src={ar} style={{rotate: "180deg", width: "20%"}} alt = {"arrow to back"} onClick={() => {
                        if (pageChoose < 4) {
                            if (Object.keys(allOpt[pageChoose - 1]).length !== 0 || allOpt[pageChoose - 1].length > 0) {
                                console.log(group.length)
                                setPageChoose(pageChoose + 1)
                            }
                        }
                        else{
                            checkService.postRegister(course, group, {...fullUserData}).then(async a => {
                                console.log(a)
                                if(a.status === 202){
                                    setPageChoose(pageChoose + 1)
                                    const mes = await checkService.postGrade(course, group, lab)
                                    console.log(mes.message)
                                }})

                        }
                    }} />
                </div>
            </div>
        </div>

    </div>
    );
}

export default App;
