import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:5000'
});
export default class checkService {
    static async getCourses(){
        const response = await api.get("/courses", {});
        console.log(response)
        return response.data
    }

    static async getGroups(course_id){
        const response = await api.get(`courses/${course_id}/groups`, {});
        return response.data
    }

    static async getLabs(course_id, groups_id){
        const response = await api.get(`courses/${course_id}/groups/${groups_id}/labs`, {});
        return response.data
    }

    static async postRegister(idCourse, idGroup, {surname, name, patronymic, github}){
        //const response = await axios.post(`courses/${idCourse}/groups/${idGroup}/register`, {
        //  name, surname, patronymic, github
        // });
        if(github === "202r" || github === "202w"){
            return {status: 202, message: "Этот аккаунт GitHub уже был указан ранее для этого же студента. Для изменения аккаунта обратитесь к преподавателю"}
        }
        return {status: 400, message: "Пользователь GitHub не найден. Проверьте никнейм github."}
    }


    //POST /courses/{course_id}/groups/{group_id}/labs/{lab_id}/grade
    static async postGrade(idCourse, idGroup, idLab, github){
        //const response = await axios.post(`courses/${idCourse}/groups/${idGroup}/labs/${idLab}/grade`, {
            //github
        // });
        if (github === "202r"){
            return {message: "Проверка пройдена успешно."}
        }
        else{
            return {message: "Проверка не пройдена."}
        }

    }
}
